import React, { useState } from "react";

const Contact = () => {
  const [data, setData] = useState({
    fullname: "",
    phone: "",
    email: "",
    msg: "",
  });
  const inputEvent = (event) => {
    const { name, value } = event.target;
    setData((preVal) => {
      return {
        ...preVal,
        [name]: value,
      };
    });
  };
  const formSubmit = (e) => {
    e.preventDefault();
    alert(`
    Name: ${data.fullname}. 
    Email: ${data.email}.
    Number:  ${data.phone}. 
     Message: ${data.msg}`);
  };
  return (
    <>
      <h1 className="text-center">Contact Us</h1>
      <div className="container">
        <div className="row">
          <div className="col-md-10">
            <div className="my-5"></div>
            <div className="contact_div">
              <div className="row">
                <div className="col-10 ">
                  <form onSubmit={formSubmit}>
                    <div class="">
                      <label for="exampleInputEmail1" class="form-label">
                        Full Name
                      </label>
                      <input
                        type="text"
                        class="form-control"
                        id="exampleInputEmail1"
                        name="fullname"
                        value={data.fullname}
                        onChange={inputEvent}
                        placeholder="Enter your name"
                        aria-describedby="emailHelp"
                      />
                    </div>
                    <div class="mb-3">
                      <label for="exampleInputEmail1" class="form-label">
                        Email address
                      </label>

                      <input
                        type="email"
                        class="form-control"
                        id="exampleInputEmail1"
                        name="email"
                        value={data.email}
                        onChange={inputEvent}
                        placeholder="name@example.com"
                        aria-describedby="emailHelp"
                      />
                    </div>
                    <div class="mb-3">
                      <label for="exampleInputEmail1" class="form-label">
                        Phone
                      </label>

                      <input
                        type="number"
                        class="form-control"
                        id="exampleInputEmail1"
                        name="phone"
                        value={data.phone}
                        onChange={inputEvent}
                        placeholder="mobile no"
                        aria-describedby="emailHelp"
                      />
                    </div>
                    <div class="mb-3">
                      <label
                        for="exampleFormControlTextarea1"
                        class="form-label"
                      >
                        Message
                      </label>
                      <textarea
                        class="form-control"
                        id="exampleFormControlTextarea1"
                        name="msg"
                        value={data.msg}
                        onChange={inputEvent}
                        rows="3"
                      ></textarea>
                    </div>
                    <div className="col-12">
                      <button type="submit" class="btn btn-outline-primary">
                        Submit form
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Contact;
